// src/contexts/auth.tsx
import { createContext, useContext, useEffect, useState } from 'react';
import { apiClient } from '../services/apiClient';
import { getMe } from '../services/api';
import { initData } from '@telegram-apps/sdk';
import type { UserRole } from '../types/UserRole';

interface AuthContextType {
  userId: string | null;
  role: UserRole | null;
  isBanned: boolean;
  loading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [userId, setUserId] = useState<string | null>(null);
  const [role, setRole] = useState<UserRole | null>(null);
  const [isBanned, setIsBanned] = useState<boolean>(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const authenticateUser = async () => {
      initData.restore();
      const data = initData.raw();
      if (!data) {
        setLoading(false);
        return;
      }
      try {
        const { data: auth } = await apiClient.post('/auth/telegram', { initData: data });
        localStorage.setItem('authToken', auth.access_token);
        localStorage.setItem('refreshToken', auth.refresh_token);
        apiClient.defaults.headers.common.Authorization = `Bearer ${auth.access_token}`;

        const me = await getMe();
        setUserId(me.id);
        setRole(me.role);
        // –û–±—Ä–∞—Ç–∏—Ç–µ –≤–Ω–∏–º–∞–Ω–∏–µ: –ø–æ–ª–µ –∏–∑ API –¥–æ–ª–∂–Ω–æ —Ç–æ—á–Ω–æ —Å–æ–≤–ø–∞–¥–∞—Ç—å.
        // –ß–∞—Å—Ç–æ –æ–Ω–æ –Ω–∞–∑—ã–≤–∞–µ—Ç—Å—è is_banned, –Ω–æ –≤ JS —É–¥–æ–±–Ω–µ–µ ‚Äî isBanned.
        setIsBanned(!!me.is_banned);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    authenticateUser();
  }, []);
  console.log('AuthContext:', { userId, role, isBanned, loading });

  return (
    <AuthContext.Provider value={{ userId, role, isBanned, loading }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const ctx = useContext(AuthContext);
  if (!ctx) throw new Error('useAuth must be used within AuthProvider');
  return ctx;
};

import React from 'react';
import { useAuth } from './auth';

export const AuthGate: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { loading } = useAuth();

  if (loading) return <div className="fixed inset-0 z-50 flex items-center justify-center">
        <div className="h-10 w-10 rounded-full border-4 border-gray-300 border-t-gray-600 always-spin" />
      </div>;    // –ø–æ–∫–∞ —Ç–æ–∫–µ–Ω –ø–æ–ª—É—á–∞–µ–º ‚Äî –ø—Ä–æ—Å—Ç–æ –∫—Ä—É—Ç–∏–º –ª–æ–∞–¥–µ—Ä
  return <>{children}</>;
};

export function normalizeLineBreaksToDivBr(html: string): string {
  try {
    const wrapper = document.createElement('div');
    wrapper.innerHTML = html;

    const BLOCK_TAGS = new Set([
      'DIV','P','SECTION','ARTICLE','UL','OL','LI','H1','H2','H3','H4','H5','H6','TABLE','THEAD','TBODY','TFOOT','TR','TD','TH','BLOCKQUOTE','PRE'
    ]);

    const isBlock = (el: Element | null) => !!el && BLOCK_TAGS.has(el.tagName);

    const isVisuallyEmpty = (el: HTMLElement) => {
      try {
        // —Å—á–∏—Ç–∞–µ–º –ø—É—Å—Ç—ã–º–∏: —Ç–æ–ª—å–∫–æ –ø—Ä–æ–±–µ–ª—ã/&nbsp;/–±—Ä–µ–π–∫–∏/–ø—É—Å—Ç—ã–µ –∏–Ω–ª–∞–π–Ω—ã
        const clone = el.cloneNode(true) as HTMLElement;
        // —É–±–∏—Ä–∞–µ–º –≤—Å–µ –∏–Ω–ª–∞–π–Ω—ã-–æ–±—ë—Ä—Ç–∫–∏ –±–µ–∑ —Ç–µ–∫—Å—Ç–∞
        clone.querySelectorAll('b,i,span,strong,em,a,small,u,s,mark,code').forEach(n => {
          if (n.textContent?.trim() === '' && n.querySelectorAll('img, svg, video, audio').length === 0) {
            n.remove();
          }
        });
        const txt = clone.innerHTML
          .replace(/\u00A0/g, ' ')
          .replace(/<br\s*\/?>/gi, '\n')
          .replace(/[\s\n]+/g, '');
        return txt.length === 0;
      } catch (error) {
        console.warn('Error in isVisuallyEmpty:', error);
        return false;
      }
    };

    // 1) P ‚Üí DIV (–∏ –ø—É—Å—Ç—ã–µ P ‚Üí <div><br></div>)
    const paragraphs = Array.from(wrapper.querySelectorAll('p'));
    paragraphs.forEach((p) => {
      try {
        const hasOnlyBr =
          p.childNodes.length === 1 &&
          p.firstChild?.nodeName.toLowerCase() === 'br';

        if (hasOnlyBr || isVisuallyEmpty(p as HTMLElement)) {
          const div = document.createElement('div');
          div.appendChild(document.createElement('br'));
          p.replaceWith(div);
          return;
        }

        const div = document.createElement('div');
        while (p.firstChild) div.appendChild(p.firstChild);
        p.replaceWith(div);
      } catch (error) {
        console.warn('Error processing paragraph:', error);
      }
    });

    // 2) –ö–∞–∂–¥—ã–π <br> ‚Üí –æ—Ç–¥–µ–ª—å–Ω—ã–π <div><br></div>, –≤—Å—Ç–∞–≤–ª—è—è —É –±–ª–∏–∂–∞–π—à–µ–≥–æ –±–ª–æ—á–Ω–æ–≥–æ –ø—Ä–µ–¥–∫–∞
    const allBr = Array.from(wrapper.querySelectorAll('br'));
    allBr.forEach((br) => {
      try {
        // —É–∂–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Å–ª—É—á–∞–π: <div><br></div>
        const parent = br.parentElement;
        if (
          parent &&
          parent.tagName === 'DIV' &&
          parent.childNodes.length === 1 &&
          parent.firstChild === br
        ) {
          return;
        }

        // –Ω–∞–π–¥—ë–º –±–ª–∏–∂–∞–π—à–µ–≥–æ –±–ª–æ—á–Ω–æ–≥–æ –ø—Ä–µ–¥–∫–∞ –∏ –ø–æ–∑–∏—Ü–∏—é –≤—Å—Ç–∞–≤–∫–∏
        let blockAncestor: HTMLElement | null = parent as HTMLElement | null;
        let anchor: Node = br; // –∫—É–¥–∞ –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω–æ —ç—Ç–æ–≥–æ —É–∑–ª–∞ –±—É–¥–µ–º –≤—Å—Ç–∞–≤–ª—è—Ç—å
        while (blockAncestor && !isBlock(blockAncestor)) {
          anchor = blockAncestor; // –ø–æ–¥–Ω–∏–º–∞–µ–º ¬´—è–∫–æ—Ä—å¬ª
          blockAncestor = blockAncestor.parentElement as HTMLElement | null;
        }
        if (!blockAncestor) {
          // –Ω–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π ‚Äî –µ—Å–ª–∏ –≤–¥—Ä—É–≥ –Ω–µ—Ç –±–ª–æ—á–Ω–æ–≥–æ –ø—Ä–µ–¥–∫–∞, –≤—Å—Ç–∞–≤–∏–º –≤ –∫–æ—Ä–µ–Ω—å
          blockAncestor = wrapper;
          anchor = br;
        }

        // —Å–æ–±–µ—Ä—ë–º –ø–æ–¥—Ä—è–¥ –∏–¥—É—â–∏–µ <br> (–ø–∞—á–∫—É)
        const brsInARow: HTMLBRElement[] = [br];
        let next = br.nextSibling;
        while (next && next.nodeName.toLowerCase() === 'br') {
          brsInARow.push(next as HTMLBRElement);
          next = next.nextSibling;
        }

        // –Ω–∞ –ø–æ–∑–∏—Ü–∏—é ANCHOR –ø–µ—Ä–µ–¥–≤–∏–Ω–µ–º –ø–∞—á–∫—É –∫–∞–∫ –Ω–∞–±–æ—Ä –±–ª–æ–∫–æ–≤
        brsInARow.forEach((oneBr) => {
          try {
            const div = document.createElement('div');
            div.appendChild(document.createElement('br'));
            blockAncestor!.insertBefore(div, anchor);
            oneBr.remove();
          } catch (error) {
            console.warn('Error processing br element:', error);
          }
        });

        // –µ—Å–ª–∏ –∏—Å—Ö–æ–¥–Ω—ã–π parent —Å—Ç–∞–ª –ø—É—Å—Ç—ã–º –∏–Ω–ª–∞–π–Ω–æ–º ‚Äî –æ—á–∏—Å—Ç–∏–º
        if (parent && !isBlock(parent) && isVisuallyEmpty(parent)) {
          try {
            parent.remove();
          } catch (error) {
            console.warn('Error removing empty parent:', error);
          }
        }
      } catch (error) {
        console.warn('Error processing br:', error);
      }
    });

    // 3) –£–¥–∞–ª–∏—Ç—å –ø—É—Å—Ç—ã–µ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä—ã, –∫—Ä–æ–º–µ —Ä–æ–≤–Ω–æ <div><br></div>
    const allElements = Array.from(wrapper.querySelectorAll('*'));
    allElements.forEach((el) => {
      try {
        if (!(el instanceof HTMLElement)) return;

        if (el.tagName === 'DIV') {
          const okDiv =
            el.childNodes.length === 1 &&
            el.firstChild?.nodeName.toLowerCase() === 'br';
          if (okDiv) return;
        }
        // –ø—É—Å—Ç—ã–µ –∏–Ω–ª–∞–π–Ω—ã/–±–ª–æ–∫–∏ ‚Äî —É–±—Ä–∞—Ç—å
        if (isVisuallyEmpty(el)) {
          el.remove();
        }
      } catch (error) {
        console.warn('Error processing element:', error);
      }
    });

    // 4) –ù–æ—Ä–º–∞–ª–∏–∑–æ–≤–∞—Ç—å &nbsp; –≤ —Ç–µ–∫—Å—Ç–æ–≤—ã—Ö —É–∑–ª–∞—Ö
    const allElementsForText = Array.from(wrapper.querySelectorAll('*'));
    allElementsForText.forEach((el) => {
      try {
        el.childNodes.forEach((n) => {
          if (n.nodeType === Node.TEXT_NODE) {
            n.nodeValue = (n.nodeValue || '').replace(/\u00A0/g, ' ');
          }
        });
      } catch (error) {
        console.warn('Error processing text nodes:', error);
      }
    });

    return wrapper.innerHTML;
  } catch (error) {
    console.error('Error in normalizeLineBreaksToDivBr:', error);
    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∏—Å—Ö–æ–¥–Ω—ã–π HTML –≤ —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏
    return html;
  }
}

