export default function PostTemplatesPage() {
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const navigate = useNavigate();

  const handleDeleteTemplate = async (post: Post) => {
    if (!window.confirm(`–£–¥–∞–ª–∏—Ç—å —à–∞–±–ª–æ–Ω "${post.name}"?`)) return;
    try {
      await updatePost(
        post.id,
        null,
        false
      );
      setPosts(prev => prev.filter(p => p.id !== post.id));
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —à–∞–±–ª–æ–Ω–∞:', err);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å —à–∞–±–ª–æ–Ω.');
    }
  };

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const data = await getTemplates();
        setPosts(data);
      } catch (err) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —à–∞–±–ª–æ–Ω–æ–≤:', err);
        // —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ —É–∂–µ –ø–æ–∫–∞–∑–∞–Ω–æ —Ä–µ—Ç—Ä–∞—è–º–∏ –≤–Ω—É—Ç—Ä–∏ getTemplates; –æ—Å—Ç–∞–≤–∏–º –º—è–≥–∫–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const handleUseTemplate = (post: Post) => {
    navigate('/posts', { state: { template: post } });
  };

  useEffect(() => {
    const removeBack = on("back_button_pressed", () => navigate(`/`));
    return () => removeBack();

}, [navigate]);

  return (
    <div className="container mx-auto p-4 bg-brandlight min-h-screen">

      <div className="flex space-x-8 mb-6">
  {(["schedule", "create", "templates"] as const).map(tab => (
    <button
      key={tab}
      onClick={() => {
        if (tab === "schedule")        navigate("/posts");                     // ¬´–†–∞—Å–ø–∏—Å–∞–Ω–∏–µ¬ª
        else if (tab === "create")     navigate("/posts", { state: { openCreate: true } });
      }}
      className={`transition pb-1 ${
        tab === "templates"
          ? "text-2xl font-semibold text-brand border-b-2 border-brand"        // –∞–∫—Ç–∏–≤–Ω–∞—è
          : "text-xl text-gray-600"
      }`}
    >
      {tab === "schedule" ? "–†–∞—Å–ø–∏—Å–∞–Ω–∏–µ" : tab === "create" ? "–°–æ–∑–¥–∞—Ç—å –ø–æ—Å—Ç" : "–®–∞–±–ª–æ–Ω—ã"}
    </button>
  ))}
</div>
      <h1 className="text-2xl font-semibold text-brand mb-6">–®–∞–±–ª–æ–Ω—ã –ø–æ—Å—Ç–æ–≤</h1>
      {loading && (
        <div className="flex justify-center py-10">
          <div className="h-10 w-10 border-4 border-brand border-t-transparent rounded-full animate-spin" aria-label="–ó–∞–≥—Ä—É–∑–∫–∞ —à–∞–±–ª–æ–Ω–æ–≤"/>
        </div>
      )}
      {!loading && (posts.length === 0 ? (
        <div className="text-gray-500 italic">–®–∞–±–ª–æ–Ω—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {posts.map(post => (
            <div
              key={post.id}
              className="relative bg-white rounded-2xl shadow p-4 flex flex-col"
            >
              <img
                src="/icons/trash.png"
                alt="–£–¥–∞–ª–∏—Ç—å —à–∞–±–ª–æ–Ω"
                className="absolute top-2 right-2 h-6 w-6 cursor-pointer opacity-60 hover:opacity-100 transition"
                onClick={e => { e.stopPropagation(); handleDeleteTemplate(post); }}
              />
              {post.image_path && (
                <img
                  src={post.image_path}
                  alt={post.name}
                  className="h-40 w-full object-cover rounded-lg mb-4"
