    useEffect(() => {
        if (template) {
            setTitle(template.name);
            setEditorText(template.text);
            setEditorHtml(template.html || "");
            setEditorEntities(template.entities || []);
            if (template.image_path) setPhotoPreview(template.image_path);
            setIsTemplate(template.is_template);
            setActiveTab("create");
        }
    }, [template]);


    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    function formatLocalDate(d: Date): string {
        const Y = d.getFullYear();
        const M = String(d.getMonth() + 1).padStart(2, "0");
        const D = String(d.getDate()).padStart(2, "0");
        return `${Y}-${M}-${D}`;        // YYYY-MM-DD (–ª–æ–∫–∞–ª—å–Ω–∞—è –¥–∞—Ç–∞)
    }

    function formatLocalTimeWithSeconds(d: Date): string {
        const h = String(d.getHours()).padStart(2, "0");
        const m = String(d.getMinutes()).padStart(2, "0");
        return `${h}:${m}:00`;          // HH:mm:ss
    }
    const filteredChats = useMemo(
        () =>
            chats.filter((c) =>
                c.name.toLowerCase().includes(chatSearch.trim().toLowerCase())
            ),
        [chatSearch, chats]
    );

    const groupedFilteredChats = useMemo(() => {
        const byType = new Map<string, ChatItem[]>();
        for (const chat of filteredChats) {
            const typeId = chat.chat_type_id ?? "__none__";
            if (!byType.has(typeId)) byType.set(typeId, []);
            byType.get(typeId)!.push(chat);
        }
        const typeNameById = new Map<string, string>(chatTypes.map(ct => [ct.id, ct.name]));
        const result = Array.from(byType.entries()).map(([typeId, list]) => ({
            typeId,
            typeName: typeId === "__none__" ? "–ë–µ–∑ –≥—Ä—É–ø–ø—ã" : (typeNameById.get(typeId) ?? "–î—Ä—É–≥–∞—è –≥—Ä—É–ø–ø–∞"),
            chats: list.sort((a, b) => a.name.localeCompare(b.name)),
        }));
        // —Å–æ—Ä—Ç–∏—Ä—É–µ–º –≥—Ä—É–ø–ø—ã: —Å–Ω–∞—á–∞–ª–∞ –∏–º–µ–Ω–æ–≤–∞–Ω–Ω—ã–µ –ø–æ –∞–ª—Ñ–∞–≤–∏—Ç—É, –∑–∞—Ç–µ–º "–ë–µ–∑ –≥—Ä—É–ø–ø—ã"
        result.sort((a, b) => {
            if (a.typeId === "__none__" && b.typeId !== "__none__") return 1;
            if (a.typeId !== "__none__" && b.typeId === "__none__") return -1;
            return a.typeName.localeCompare(b.typeName);
        });
        return result;
    }, [filteredChats, chatTypes]);

    const handleChatToggle = (chatId: string) => {
        setSelectedChats((prev) =>
            prev.includes(chatId) ? prev.filter((c) => c !== chatId) : [...prev, chatId]
        );
    };

    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Schedule fetch ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    const fetchSchedule = useCallback(async () => {
        try {
            setIsLoading(true);
            const raw = await getPostsToPublish();

            // 1) –æ–±—â–∏–π —Ñ–∏–ª—å—Ç—Ä –ø–æ –º–µ–Ω–µ–¥–∂–µ—Ä—É/—á–∞—Ç-—Ç–∏–ø—É/—á–∞—Ç—É
            const pre = raw
                .filter(p => {
                    if (managerFilter && p.responsible_manager_id !== managerFilter) return false;
                    if (chatTypeFilter && !p.chats.some(c => c.chat_type_id === chatTypeFilter)) return false;
                    if (chatFilter && !p.chats.some(c => c.id === chatFilter)) return false;
                    return true;
                });

            // 2) —Å–æ–±–∏—Ä–∞–µ–º –∑–∞–≥–æ–ª–æ–≤–∫–∏ –∏–∑ —É–∂–µ –≤–ª–æ–∂–µ–Ω–Ω–æ–≥–æ p.post (–∏–∑–±–µ–≥–∞–µ–º N+1)
            const titles = new Map<string, string>();
            for (const p of pre) {
                if (!titles.has(p.post_id) && p.post?.name) {
                    titles.set(p.post_id, p.post.name);
                }
            }

            const map: Record<string, EventItem[]> = {};

            for (const p of pre) {
                const base: EventItem = {
                    id: p.id,
                    postId: p.post_id,
                    title: titles.get(p.post_id)!,
                    time: p.scheduled_time.slice(0, 5),
                    scheduledType: p.scheduled_type
                };
                const now = new Date();


                if (p.scheduled_type === "single") {
                    if (!p.scheduled_date) continue;
                    const dt = new Date(`${p.scheduled_date}T${p.scheduled_time}`);
                    if (dt < now) continue;            // <-- –æ—Ç—Ä–µ–∑–∞–µ–º –ø—Ä–æ—à–ª—ã–µ
                    const isoLocal = dt.toLocaleDateString("sv-SE");
                    map[isoLocal] = (map[isoLocal] || []).concat(base);
                } else {
                    for (let i = 0; i < 7; i++) {
                        const baseDay = new Date();
                        baseDay.setHours(0, 0, 0, 0);
                        baseDay.setDate(baseDay.getDate() + i);
                        const isoLocal = baseDay.toLocaleDateString("sv-SE");
                        const dt = new Date(`${isoLocal}T${p.scheduled_time}`);
                        if (dt < now) continue;          // <-- –æ—Ç—Ä–µ–∑–∞–µ–º –ø—Ä–æ—à–ª—ã–µ
                        map[isoLocal] = (map[isoLocal] || []).concat(base);
                    }
                }

            }

            // 5) —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ –≤—Ä–µ–º–µ–Ω–∏
            Object.values(map).forEach(arr =>
                arr.sort((a, b) => a.time.localeCompare(b.time))
            );

            setSchedule(map);
            const todayIsoSent = new Date().toLocaleDateString("sv-SE");
            setOpenDays(Object.fromEntries(Object.keys(map).map(d => [d, d === todayIsoSent])));
        } catch (err) {
            console.error(err);
            alert("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –∑–∞–ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –ø–æ—Å—Ç—ã");
        } finally {
            setIsLoading(false);
        }
    }, [managerFilter, chatTypeFilter, chatFilter]);

    const fetchSent = useCallback(async () => {
        try {
            setIsLoading(true);
            const raw = await getPostsToPublish();

            // 1) –æ–±—â–∏–π —Ñ–∏–ª—å—Ç—Ä –ø–æ –º–µ–Ω–µ–¥–∂–µ—Ä—É/—á–∞—Ç-—Ç–∏–ø—É/—á–∞—Ç—É
            const pre = raw.filter(p => {
                if (managerFilter && p.responsible_manager_id !== managerFilter) return false;
                if (chatTypeFilter && !p.chats.some(c => c.chat_type_id === chatTypeFilter)) return false;
                if (chatFilter && !p.chats.some(c => c.id === chatFilter)) return false;
                return true;
            });

            // 2) —Å–æ–±–∏—Ä–∞–µ–º –∑–∞–≥–æ–ª–æ–≤–∫–∏ –∏–∑ –≤–ª–æ–∂–µ–Ω–Ω–æ–≥–æ p.post (–±–µ–∑ –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã—Ö –∑–∞–ø—Ä–æ—Å–æ–≤)
            const titles = new Map<string, string>();
            for (const p of pre) {
                if (!titles.has(p.post_id) && p.post?.name) {
                    titles.set(p.post_id, p.post.name);
                }
            }

            const map: Record<string, EventItem[]> = {};
            const now = new Date();


            // 3) –æ–¥–∏–Ω–æ—á–Ω—ã–µ –¥–æ —Ç–µ–∫—É—â–µ–≥–æ –º–æ–º–µ–Ω—Ç–∞ (–ø—Ä–æ—à–µ–¥—à–∏–µ)
            pre
                .filter(p => p.scheduled_type === "single" && p.scheduled_date)
                .forEach(p => {
                    const dt = new Date(`${p.scheduled_date!}T${p.scheduled_time}`);
                    if (dt >= now) return;           // <-- –æ—Å—Ç–∞—é—Ç—Å—è —Ç–æ–ª—å–∫–æ –ø—Ä–æ—à–ª—ã–µ
                    const iso = dt.toLocaleDateString("sv-SE");
                    map[iso] = (map[iso] || []).concat({
                        id: p.id,
                        postId: p.post_id,
                        title: titles.get(p.post_id)!,
                        time: p.scheduled_time.slice(0, 5),
                        scheduledType: "single"
                    });
                });

            // 4) –µ–∂–µ–¥–Ω–µ–≤–Ω—ã–µ: —Å–µ–≥–æ–¥–Ω—è—à–Ω–∏–π –¥–µ–Ω—å –∏ –ø—Ä–µ–¥—ã–¥—É—â–∏–µ 6 (–≤—Å–µ–≥–æ 7), –Ω–æ –Ω–µ —Ä–∞–Ω—å—à–µ created_at –∑–∞–ø–∏—Å–∏
            pre
                .filter(p => p.scheduled_type === "everyday")
                .forEach(p => {
                    // –¥–∞—Ç–∞ —Å–æ–∑–¥–∞–Ω–∏—è –∑–∞–ø–∏—Å–∏ ‚Äî –æ–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –∏—Å—Ç–æ—Ä–∏—é –Ω–µ —Å—Ç–∞—Ä—à–µ –Ω–µ—ë
                    const created = new Date(p.created_at);
                    created.setHours(0, 0, 0, 0);
                    // –≤–∫–ª—é—á–∞–µ–º —Å–µ–≥–æ–¥–Ω—è—à–Ω–∏–π –¥–µ–Ω—å (i = 0), –µ—Å–ª–∏ –≤—Ä–µ–º—è —É–∂–µ –ø—Ä–æ—à–ª–æ
                    for (let i = 0; i < 7; i++) {
                        const baseDay = new Date();
                        baseDay.setHours(0, 0, 0, 0);
                        baseDay.setDate(baseDay.getDate() - i);
                        if (baseDay < created) break;   // –Ω–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –¥–Ω–∏ —Ä–∞–Ω–µ–µ —Å–æ–∑–¥–∞–Ω–∏—è –∑–∞–ø–∏—Å–∏
                        const iso = baseDay.toLocaleDateString("sv-SE");
                        const dt = new Date(`${iso}T${p.scheduled_time}`);
                        if (dt >= now) continue;       // <-- –æ—Å—Ç–∞–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ –ø—Ä–æ—à–ª—ã–µ
                        map[iso] = (map[iso] || []).concat({
                            id: p.id,
                            postId: p.post_id,
                            title: titles.get(p.post_id)!,
                            time: p.scheduled_time.slice(0, 5),
                            scheduledType: "everyday"
                        });
                    }
                });

            // 5) —É–¥–∞–ª—è–µ–º –ª—é–±—ã–µ –±—É–¥—É—â–∏–µ –¥–∞—Ç—ã (–¥–ª—è –≤–∫–ª–∞–¥–∫–∏ ¬´–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–µ¬ª –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å —Ç–æ–ª—å–∫–æ –ø—Ä–æ—à–µ–¥—à–∏–µ)
            const todayIsoFilter = new Date().toLocaleDateString("sv-SE");
            Object.keys(map).forEach((iso) => {
                if (iso > todayIsoFilter) delete map[iso];
            });

            // 6) —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞
            Object.values(map).forEach(arr =>
                arr.sort((a, b) => a.time.localeCompare(b.time))
            );

            setSchedule(map);
            const todayIsoOpen = new Date().toLocaleDateString("sv-SE");
            setOpenDays(Object.fromEntries(Object.keys(map).map(d => [d, d === todayIsoOpen])));
        } catch (err) {
            console.error(err);
            alert("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–µ –ø–æ—Å—Ç—ã");
        } finally {
            setIsLoading(false);
        }
    }, [managerFilter, chatTypeFilter, chatFilter]);


    useEffect(() => {
        if (viewMode === "scheduled") {
            fetchSchedule();
        } else {
            fetchSent();
        }
    }, [viewMode, fetchSchedule, fetchSent]);


    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Chats fetch ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    const fetchChats = useCallback(async () => {
        try {
            const data = await getChats();
            setChats(data);
        } catch (err) {
            console.error("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–ø–∏—Å–æ–∫ —á–∞—Ç–æ–≤", err);
        }
    }, []);

    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ run fetches ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    useEffect(() => {
        fetchChats();
    }, []);


    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Back button ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    useEffect(() => {
        const remove = on("back_button_pressed", () => navigate(`/`));
        return () => remove();
    }, [navigate]);

    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Save Post ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    const handleSave = async () => {
        // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–µ –æ—à–∏–±–∫–∏
        setTitleError("");
        setTextError("");

        // –í–∞–ª–∏–¥–∞—Ü–∏—è
        let hasErrors = false;
        if (!title.trim()) {
            setTitleError("–í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ –ø–æ—Å—Ç–∞");
            hasErrors = true;
        }
        if (!editorText.trim()) {
            setTextError("–í–≤–µ–¥–∏—Ç–µ —Ç–µ–∫—Å—Ç –ø–æ—Å—Ç–∞");
            hasErrors = true;
        }
        if (scheduleType === "once" && !scheduledAt) {
            alert("–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è");
            return;
        }
        if (scheduleType === "daily" && !timeOnly) {
            alert("–í—ã–±–µ—Ä–∏—Ç–µ –≤—Ä–µ–º—è –¥–ª—è –µ–∂–µ–¥–Ω–µ–≤–Ω–æ–π —Ä–∞—Å—Å—ã–ª–∫–∏");
            return;
        }
        if (!userId) {
            alert("–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –º–µ–Ω–µ–¥–∂–µ—Ä–∞");
            return;
        }
        if (!responsibleManagerId) {
            alert("–í—ã–±–µ—Ä–∏—Ç–µ –æ—Ç–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω–æ–≥–æ –º–µ–Ω–µ–¥–∂–µ—Ä–∞");
            return;
        }

        if (hasErrors) {
            // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–µ—Ä–≤–æ–º –ø–æ–ª–µ —Å –æ—à–∏–±–∫–æ–π
            if (!title.trim() && titleInputRef.current) {
                titleInputRef.current.focus();
            }
            return;
        }

        try {
            let postId: string;

            if (template) {
                // –ü–æ –∂–µ–ª–∞–Ω–∏—é ‚Äî –æ–±–Ω–æ–≤–ª—è–µ–º —Å–∞–º —à–∞–±–ª–æ–Ω
                if (updateTemplate) {
                    const entitiesChanged =
                        JSON.stringify(editorEntities ?? []) !== JSON.stringify(template.entities ?? []);
                    const htmlBaseline = template.html ?? template.text;
                    await updatePost(
                        template.id,
                        title !== template.name ? title : undefined,
                        isTemplate !== template.is_template ? isTemplate : undefined,
                        editorText !== template.text ? editorText : undefined,
                        editorHtml !== htmlBaseline ? editorHtml : undefined,
                        entitiesChanged ? editorEntities : undefined,
                        photoFile ?? undefined
                    );
                }

                // –°–æ–∑–¥–∞—ë–º –∫–æ–ø–∏—é –ø–æ–¥ –æ—Ç–ø—Ä–∞–≤–∫—É (–∑–∞–º–æ—Ä–æ–∑–∫–∞ –∫–æ–Ω—Ç–µ–Ω—Ç–∞)
                // –ï—Å–ª–∏ –Ω–æ–≤—ã–π —Ñ–∞–π–ª –Ω–µ –≤—ã–±—Ä–∞–Ω, –ø–µ—Ä–µ–¥–∞–¥–∏–º image_path —à–∞–±–ª–æ–Ω–∞ –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —Ñ–æ—Ç–æ
                postId = await createPost(
                    title,
                    editorText,
                    false, // –∫–ª–æ–Ω –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –Ω–µ —è–≤–ª—è–µ—Ç—Å—è —à–∞–±–ª–æ–Ω–æ–º
                    editorHtml,
                    editorEntities,
                    photoFile ?? undefined,
                    (!photoFile && template.image_path) ? template.image_path : undefined,
                );
            } else {
                // —Å–æ–∑–¥–∞—ë–º –æ–±—ã—á–Ω—ã–π –ø–æ—Å—Ç (–∫–∞–∫ –±—ã–ª–æ)
                postId = await createPost(
                    title,
                    editorText,
                    isTemplate,
                    editorHtml,
                    editorEntities,
                    photoFile ?? undefined,
                    undefined,
                );
            }
            let scheduled_date: string | null = null;
            let scheduled_time: string;

            if (scheduleType === "once") {
                scheduled_date = formatLocalDate(scheduledAt!);
                scheduled_time = formatLocalTimeWithSeconds(scheduledAt!);
            } else {
                scheduled_time = formatLocalTimeWithSeconds(timeOnly!);
            }

            const dto: CreatePostToPublishDTO = {
                post_id: postId,
                scheduled_type: scheduleType === "once" ? "single" : "everyday",
                responsible_manager_id: responsibleManagerId,
                scheduled_date,
                scheduled_time,
                chat_ids: selectedChats,
                manager_id: userId,
                status: "pending"
            };

            await createPostToPublish(dto);
            await fetchSchedule();

            /* reset */
            setPhotoFile(null);
            setPhotoPreview(null);
            setTitle("");
            setTitleError("");
            setTextError("");
            setEditorText("");
            setEditorHtml("");
            setEditorEntities([]);
            setScheduledAt(null);
            setTimeOnly(null);
            setScheduleType("once");
            setSelectedChats([]);
            setIsTemplate(false);
            setChatSearch("");
            setActiveTab("schedule");
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –ø–æ—Å—Ç–∞:", err);
            alert("–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –ø–æ—Å—Ç.");
        }
    };


    /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ Delete Schedule Item ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */
    const handleDelete = async (day: string, ev: EventItem) => {
        if (!window.confirm(`–£–¥–∞–ª–∏—Ç—å –ø–æ—Å—Ç ¬´${ev.title}¬ª –∏–∑ —Ä–∞—Å–ø–∏—Å–∞–Ω–∏—è?`)) return;
        try {
            await deletePostToPublish(ev.id);
            setSchedule((prev) => {
                const next = {...prev};
                next[day] = next[day].filter((i) => i.id !== ev.id);
                if (next[day].length === 0) delete next[day];
                return next;
            });
        } catch {
            alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏.");
        }
    };

    const toggleDay = (d: string) =>
        setOpenDays((o) => ({...o, [d]: !o[d]}));

